exports.setCookies = function(cookies) {
    var list = [];
    if ( !! cookies) {
        for (var index in cookies) {
            var cookie = cookies[index];
            if (cookie['key'] && cookie['value'] && cookie['age']) {
                var cookieArr = [];
                cookieArr.push(cookie['key'] + '=' + cookie['value']);
                var domain = cookie['domain'] ? cookie['domain'] : 'fatfei.com';
                cookieArr.push('Domain=' + domain);
                var expireDate = new Date((new Date()).getTime() + cookie['age'] * 1000).toUTCString();
                cookieArr.push('Expires=' + expireDate);
                cookieArr.push('Path=/');
                list.push(cookieArr.join('; '));
            }
        }
    }
    return list;
};